function WordFrequency(o,n="desc"){return new Promise(e=>{let t=o.split(" "),c=[];for(let o=0;o<t.length;o++){let n=t[o].replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g,"").toLowerCase();""!==n&&-1===c.findIndex(o=>o.word===n)?c.push({word:n,count:1}):""!==n&&c.findIndex(o=>o.word===n)>=0&&(c[c.findIndex(o=>o.word===n)].count+=1)}e(c.sort((o,e)=>"desc"===n?o.count>e.count?-1:o.count<e.count?1:0:"asc"===n?o.count<e.count?-1:o.count>e.count?1:0:void 0))})}export{WordFrequency};
