function WordFrequency(n,o="desc"){return new Promise(t=>{let e=n.split(" "),c=[];for(let n=0;n<e.length;n++){let o=e[n].replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g,"").toLowerCase();""!==o&&-1===c.findIndex(n=>n.word===o)?c.push({word:o,count:1}):""!==o&&c.findIndex(n=>n.word===o)>=0&&(c[c.findIndex(n=>n.word===o)].count+=1)}t(c.sort((n,t)=>"desc"===o?n.count>t.count?-1:n.count<t.count?1:0:"asc"===o?n.count<t.count?-1:n.count>t.count?1:0:void 0))})}
